sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


}
var gr = g_form.getControl('u_cpf');


gr.type="cpf";






    var cpfValue = newValue;

    // Remover todos os caracteres não numéricos
    cpfValue = cpfValue.replace(/\D/g, '');

    // Adicionar pontos e traço na formatação do CPF
    cpfValue = cpfValue.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');

    // Regex para validar CPF
    var cpfRegex = /^\d{3}\.\d{3}\.\d{3}-\d{2}$/;

    console.log("Valor do CPF:", cpfValue);
    console.log("Corresponde ao regex:", cpfRegex.test(cpfValue));

    if (!cpfRegex.test(cpfValue)) {
        g_form.showFieldMsg('u_cpf', 'Por favor, insira um CPF válido.', 'error');
        //g_form.clearValue('u_cpf'); //
    } else {
        // Limpar mensagem de erro se o valor do CPF for válido
        g_form.clearMessages('u_cpf');
    }
}
