sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

var getDate = newValue;
var partesData = getDate.split('/');
var diaNascimento = parseInt(partesData[0]);
var mesNascimento = parseInt(partesData[1]) - 1; 
var anoNascimento = parseInt(partesData[2]);


var dateAtual = new Date();

var dateBirthday = new Date(anoNascimento, mesNascimento, diaNascimento);
  var diferent = dateAtual.getTime()- dateBirthday.getTime();
  var idade = Math.floor(diferent/(1000* 60* 60*24* 365.25));

  g_form.setValue('u_idade', idade);

}
