sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
 //Remoção de caracteres especiais
    var numeroLimpo = newValue.replace(/\D/g, '');

    //Formatação do número digitado
    if (/^\(\d{2}\) \d{4}-\d{4}$/.test(newValue)) {
        return;
    }

    var numeroFormatado;
 if (numeroLimpo.length === 10) {
        numeroFormatado = numeroLimpo.replace(/^(\d{2})(\d{4})(\d{4})$/, '($1) $2-$3');
    } else {

        g_form.setValue(control.name, '');

        g_form.showErrorBox(control.name, 'Número de telefone inválido. \nExemplo:\nTelefone Fixo: (XX) XXXX-XXXX');
        return;
    }

    g_form.setValue(control.name, numeroFormatado);


}
