sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Função de validação, com parametro (email)
    function isValidEmail(emailInserido) {
        // Regex para validar endereço de e-mail
        var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(emailInserido);
    }

    // Verificação para validar endereço de e-mail newValue informado na tabela
    if (!isValidEmail(newValue)) {
        // Se o endereço de e-mail for inválido, exibe uma mensagem de erro
        g_form.setValue(control.name, ''); // Limpa o campo
        g_form.showErrorBox(control.name, 'Endereço de e-mail inválido. Por favor, insira um endereço de e-mail válido.');
    }
}
