sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var getDate = newValue;
    var partesData = getDate.split('/');
    var diaNascimento = parseInt(partesData[0]);
    var mesNascimento = parseInt(partesData[1]) - 1;
    var anoNascimento = parseInt(partesData[2]);

    var dateBirthday = new Date(anoNascimento, mesNascimento, diaNascimento);
    var dateAtual = new Date();

    var diff = dateAtual.getTime() - dateBirthday.getTime();
    var idadeAnos = Math.floor(diff / (1000 * 60 * 60 * 24 * 365.25)); // Calcula anos
    var idadeMeses = Math.floor((diff % (1000 * 60 * 60 * 24 * 365.25)) / (1000 * 60 * 60 * 24 * 30.4375)); // Calcula meses
    var idadeDias = Math.floor((diff % (1000 * 60 * 60 * 24 * 365.25)) % (1000 * 60 * 60 * 24 * 30.4375) / (1000 * 60 * 60 * 24)); // Calcula dias

    var idadeFormatada = idadeAnos + ' anos, ' + idadeMeses + ' meses, ' + idadeDias + ' dias';

    g_form.setValue('u_idade', idadeFormatada);
}
