sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var cpfValue = newValue;

    // Estou removendo todos os caracteres não numéricos
    cpfValue = cpfValue.replace(/\D/g, '');

    // Adicionar pontos e traço na formatação do CPF
    cpfValue = cpfValue.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');


    var currentCpfValue = g_form.getValue('cpf');
    if (cpfValue !== currentCpfValue) {
        g_form.setValue('cpf', cpfValue);
    }

    // Regex para validar CPF
    var cpfRegex = /^\d{3}\.\d{3}\.\d{3}-\d{2}$/;

    // console.log("Valor do CPF:", cpfValue);
    // console.log("Corresponde ao regex:", cpfRegex.test(cpfValue));

    if (!cpfRegex.test(cpfValue)) {
		g_form.setValue('cpf', '');
        g_form.showFieldMsg('cpf', 'Por favor, insira um CPF válido.', 'error');
    } else {
        // Limpar mensagem de erro se o valor do CPF for válido
        g_form.clearMessages('cpf');
    }
}
