sys_script_client.script:
function onLoad() {

  function formatPhoneNumber(input) {
    // Remove todos os caracteres não numéricos
    var cleaned = ('' + input).replace(/\D/g, '');

    // Aplica a máscara
    var formatted = cleaned.replace(/(\d{2})(\d{4})(\d{4})/, '($1) $2-$3');

    return formatted;
}

// Adiciona o evento onchange para o campo
function applyMask() {
    var element = document.getElementById(''); // Substitua 'campo_telefone' pelo ID do seu campo
    if (element) {
        element.value = formatPhoneNumber(element.value);
    }
}

document.addEventListener('DOMContentLoaded', function() {
    var element = document.getElementById('celular'); // Substitua 'campo_telefone' pelo ID do seu campo
    if (element) {
        element.addEventListener('input', applyMask);
    }
});



}
