sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Remoção de caracteres especiais
    var numeroLimpo = newValue.replace(/\D/g, '');
    //Formatação do número digitado
    if (/^\(\d{2}\) \d{4}-\d{4}$/.test(newValue) || /^\(\d{2}\) \d{5}-\d{4}$/.test(newValue)) {
        return;
    }
    //Formantando os números de 10 ou 11 digitos em 2-4-4 ou 2-5-4
    var numeroFormatado;
    if (numeroLimpo.length === 10) {
        numeroFormatado = numeroLimpo.replace(/^(\d{2})(\d{4})(\d{4})$/, '($1) $2-$3');	
    } else if (numeroLimpo.length === 11) {
        numeroFormatado = numeroLimpo.replace(/^(\d{2})(\d{5})(\d{4})$/, '($1) $2-$3');
    } else {
        //Limpa campo
        g_form.setValue(control.name, '');
        //Mensagem de Erro caso número não contenha 10 ou 11 dígitos
        g_form.showErrorBox(control.name, 'Número de telefone inválido. \nExemplo:\nCelular: (XX) X XXXX-XXXX | \nTelefone Fixo: (XX) XXXX-XXXX');
        return;
    }
    //Setando número formatado
    g_form.setValue(control.name, numeroFormatado);
}
