sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Nome do campo de telefone no formulário
    var phoneNumberField = 'telefone';

    // Obtém o valor atual do campo de telefone
    var phoneNumber = g_form.getValue(phoneNumberField);
    if (phoneNumber) {
        // Remove todos os caracteres não numéricos
        var cleanedNumber = phoneNumber.replace(/\D/g, '');

        // Formatação do número
        var formattedNumber = cleanedNumber.replace(/(\d{2})(\d{1})(\d{4})(\d{4})/, "($1) $2 $3-$4");

        // Define o valor formatado no campo
        g_form.setValue(phoneNumberField, formattedNumber);
    }
}
